mapper.select:
SELECT EXISTS
(SELECT 1
FROM follow
    WHERE follower_id = #{followerId}
    AND following_id = #{followingId}
)

SELECT follower_id
FROM follow
WHERE following_id = #{userId}

SELECT following_id
FROM follow
WHERE follower_id = #{userId}

mapper.delete:
DELETE FROM follow
WHERE follower_id = #{followerId}
AND following_id = #{followingId}

mapper.select:
