mapper.select:
SELECT name
FROM country
WHERE id = #{countryId}

SELECT label
FROM travel_style
WHERE id = #{travelStyleId}

mapper.update:
UPDATE user
SET travel_status = #{travelStatus}
WHERE user_id = #{userId}

mapper.select:
SELECT EXISTS
(SELECT 1
FROM follow
    WHERE follower_id = #{followerId}
    AND following_id = #{followingId}
)

SELECT follower_id
FROM follow
WHERE following_id = #{userId}

SELECT following_id
FROM follow
WHERE follower_id = #{userId}

mapper.delete:
DELETE FROM follow
WHERE follower_id = #{followerId}
AND following_id = #{followingId}
